package main

import "testing"

func TestFindTheDifference(t *testing.T) {
	tcs := []struct {
		inS string
		inT string
		out rune
	}{
		{"abcd", "abcde", 'e'},

		{"", "a", 'a'},
		{"abcde", "fedcba", 'f'},
		{"aaaaa", "aaabaa", 'b'},
		{"gophercon", "conugopher", 'u'},
		{"", "", 't'},
	}

	for idx, tc := range tcs {
		out := findTheDifference(tc.inS, tc.inT)
		if rune(out) != tc.out {
			t.Errorf("%d. findTheDifference(%q, %q) = %q. Expected: %q", idx, tc.inS, tc.inT, rune(out), tc.out)
		}
	}
}
